def max_sublist_sum(arr):
    max_ending_here = 0
    max_so_far = -999999999999999
    for x in arr:
        max_ending_here = max_ending_here + x
        if max_ending_here < 0:
            max_ending_here = 0
        if max_so_far < max_ending_here:
            max_so_far = max_ending_here
    return max_so_far
